mapper.select:
SELECT

FROM ccp_location l
         LEFT JOIN ccp_school_campus c ON l.campus_id = c.id
         LEFT JOIN ccp_school s ON c.school_id = s.id


        AND l.campus_id = #{campusId}


        AND c.school_id = #{schoolId}


        AND l.location_name LIKE CONCAT('%', #{locationName}, '%')


        AND l.location_type = #{locationType}


        AND l.status = #{status}


ORDER BY l.create_time DESC
