mapper.select:



        and nick_name like concat('%', #{nickName}, '%')


        and phone = #{phone}


        and id_card_name like concat('%', #{idCardName}, '%')


        and id_card_number = #{idCardNumber}


        and status = #{status}


        and real_auth_status = #{realAuthStatus}


        and online_status = #{onlineStatus}


        and create_time >= #{createTimeStart}


        and create_time <= #{createTimeEnd}


order by id desc

mapper.update:
update ccp_mini_user
union_id = #{unionId}, nick_name = #{nickName}, avatar_url = #{avatarUrl}, phone = #{phone}, real_name = #{realName}, id_card_name = #{idCardName}, id_card_number = #{idCardNumber}, face_image_url = #{faceImageUrl}, face_verify_result = #{faceVerifyResult}, gender = #{gender}, status = #{status}, real_auth_status = #{realAuthStatus}, real_auth_fail_reason = #{realAuthFailReason}, real_auth_review_by = #{realAuthReviewBy}, real_auth_review_time = #{realAuthReviewTime}, admin_remark = #{adminRemark}, last_active_time = #{lastActiveTime}, online_status = #{onlineStatus}, create_time = #{createTime}, update_time = #{updateTime}
where id = #{id}
