mapper.select:
SELECT 
FROM ccp_user_campus_auth
WHERE user_id = #{userId}
  AND campus_id = #{campusId}
LIMIT 1

mapper.update:
UPDATE ccp_user_campus_auth
student_no = #{studentNo}, student_card_image_url = #{studentCardImageUrl}, personal_image_url = #{personalImageUrl}, status = #{status}, fail_reason = #{failReason}, review_by = #{reviewBy}, review_time = #{reviewTime}, update_time = #{updateTime}
WHERE id = #{id}
