mapper.select:
SELECT 
FROM ccp_user_school_auth
WHERE user_id = #{userId}
  AND school_id = #{schoolId}
LIMIT 1

mapper.update:
UPDATE ccp_user_school_auth
campus_id = #{campusId}, student_no = #{studentNo}, student_name = #{studentName}, student_card_image_url = #{studentCardImageUrl}, extra_image_url = #{extraImageUrl}, status = #{status}, fail_reason = #{failReason}, review_by = #{reviewBy}, review_time = #{reviewTime}, submit_time = #{submitTime}, update_time = #{updateTime}
WHERE id = #{id}
